class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),$("#comment-input").val(""),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"bottomRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id = "comment-${e._id}">\n    <p>\n       <div class = "space-between-comment">\n       <div id = "comment-name-dp">\n           \n       <div>\n           <img class = "Dp_post_comment" src="${e.user.avatar}" width="25"> &nbsp;\n       </div>\n       <div>\n           ${e.user.name}\n       </div>\n        \n   </div>\n   \n        <div>\n            \n                <small>\n                    <a class = "delete-comment-button" href="/comments/destroy/${e._id}"><i class="fas fa-trash"></i></a>\n                </small>\n            \n        </div>\n       </div>\n       <div id = "lalign">\n           ${e.content}\n        </div>\n      <div id = "like-comment-container">\n      <div id = "like-comment">  \n      <a class = "toggle-like-button" data-likes = "0" href="/likes/toggle/?id=${e._id}&type=Comment">\n      <i class="fas fa-thumbs-up"></i> 0 \n       </a>\n      </div> \n        \n    </p>\n</li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"bottomRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}